import React, { useState } from "react";
import "./Wheel.css";

const rewards = [
  "T·∫•t c·∫£ u·ªëng 50%",
  "U·ªëng v·ªõi ng∆∞·ªùi ch·ªâ ƒë·ªãnh",
  "B√™n tr√°i u·ªëng 50%",
  "N·ªØ u·ªëng 50%",
  "Nam u·ªëng 50%",
  "H√°t m·ªôt b√†i ho·∫∑c u·ªëng v·ªõi ng∆∞·ªùi ƒë·ªëi di·ªán",
  "Qua l∆∞·ª£t b·∫±ng c√°ch ch·ªâ ƒë·ªãnh ng∆∞·ªùi kh√°c u·ªëng",
  "Qua l∆∞·ª£t",
  "L·∫Øc m√¥ng v√†i c√°i r·ªìi u·ªëng 50%",
  "V·ª´a u·ªëng 50% v·ª´a k√™u meow meow"
];

const Wheel = () => {
  const [isSpinning, setIsSpinning] = useState(false);
  const [rotation, setRotation] = useState(0);
  const [result, setResult] = useState("");

  const handleSpin = () => {
    if (isSpinning) return;

    const rewardCount = rewards.length;
    const selected = Math.floor(Math.random() * rewardCount);
    const anglePerSegment = 360 / rewardCount;
    const rotateDeg = 360 * 5 + (360 - anglePerSegment * selected - anglePerSegment / 2);

    setIsSpinning(true);
    setRotation(prev => prev + rotateDeg);

    setTimeout(() => {
      setIsSpinning(false);
      setResult(rewards[selected]);
    }, 4000);
  };

  return (
    <div className="flex flex-col items-center mt-12">
      <div
        className="wheel transition-all duration-[4000ms] ease-out"
        style={{ transform: `rotate(${rotation}deg)` }}
      >
        {rewards.map((reward, idx) => (
          <div
            key={idx}
            className="segment"
            style={{
              transform: `rotate(${(360 / rewards.length) * idx}deg) skewY(-60deg)`
            }}
          >
            <span className="text-xs">{reward}</span>
          </div>
        ))}
      </div>
      <div className="pointer mt-4 text-2xl">‚ñ≤</div>
      <button
        onClick={handleSpin}
        disabled={isSpinning}
        className="mt-4 px-6 py-2 bg-red-500 text-white rounded hover:bg-red-600 disabled:opacity-50"
      >
        {isSpinning ? "ƒêang quay..." : "Quay ngay üçª"}
      </button>
      {result && !isSpinning && (
        <p className="mt-6 text-center text-lg font-semibold text-pink-700">
          üéâ K·∫øt qu·∫£: <br /> <span className="text-xl">{result}</span>
        </p>
      )}
    </div>
  );
};

export default Wheel;
